from typing import List, Optional

def longest(strings: List[str]) -> Optional[str]:
    """Return the longest string from the given list. If there are multiple strings of the same length, return the first one."""
    if not strings:
        return None
    maxlen = max((len(s) for s in strings))
    for s in strings:
        if len(s) == maxlen:
            return s
    return None

